using System;using Presentation.ViewModels;using ReactiveUI;using WorkPlaceView = Presentation.Views.WorkPlaceView;namespace Presentation;public class AppViewLocator : IViewLocator{    public IViewFor ResolveView<T>(T viewModel, string contract = null)    {        return viewModel switch        {            WorkPlaceViewModel context => new WorkPlaceView { DataContext = context },            _ => throw new ArgumentOutOfRangeException(nameof(viewModel), viewModel, null)        };    }}