using System;using ReactiveUI;using StyleConfiguration.ViewModels;using StyleConfiguration.Views;namespace StyleConfiguration;public class AppViewLocator : IViewLocator{    public IViewFor ResolveView<T>(T viewModel, string contract = null)    {        return viewModel switch        {            WorkPlaceViewModel context => new WorkPlaceView {DataContext = context},            _ => throw new ArgumentOutOfRangeException(nameof(viewModel), viewModel, null)        };    }}