using System;using System.Collections.Generic;using System.IO;namespace StyleConfiguration.Config;public class UserChecker{    private string _password;    private string _login;    private string _pathToConfig;    private StreamReader _sr = new StreamReader("/home/darling/RiderProjects/StyleConfiguration/StyleConfiguration/Config/test.txt");        public UserChecker(string password, string login)    {        _password = password;        _login = login;    }    public bool CmpUsers()    {        string? line;        line = _sr.ReadLine();                if (line == null) return false;                string[]? splitLine = line.Split();        while (line != null)        {            if (splitLine.Length == 3)            {                if (String.CompareOrdinal(_login, splitLine[0]) == 0 && String.CompareOrdinal(_password, splitLine[1]) == 0                    && String.IsNullOrWhiteSpace(splitLine[2]) == false)                {                    _pathToConfig = splitLine[2];                    return true;                }            }            line = _sr.ReadLine();            if (line != null) splitLine = line.Split();        }        return false;    }    public void Test()    {        StreamReader streamReader = new StreamReader(_pathToConfig);        string line = streamReader.ReadLine();         Console.WriteLine(line);    }}