using System;using System.IO;using static System.String;namespace StyleConfiguration.Config;public class UserChecker{    private readonly string? _password;    private readonly string? _login;    public string? PathToConfig;    private readonly StreamReader _sr;    public UserChecker(string pathToUsersFile ,string? password, string? login)    {        _password = password;        _login = login;        _sr = new StreamReader(pathToUsersFile);    }    public void CompareUsers()    {        var line = _sr.ReadLine();        if (line == null)        {             throw new Exception("Файл пуст или заполнен неверно");        }        var splitLine = line.Split();        while (line != null)        {            if (splitLine.Length == 3)            {                if (CompareOrdinal(_login, splitLine[0]) == 0 && CompareOrdinal(_password, splitLine[1]) == 0                                                               && IsNullOrWhiteSpace(splitLine[2]) == false)                {                    PathToConfig = splitLine[2];                    return;                }            }            line = _sr.ReadLine();            if (line != null) splitLine = line.Split();        }        throw new Exception("Неверно введен логин или пароль");    }}