using System;using System.Reactive;using System.Reactive.Linq;using Avalonia.Interactivity;using ReactiveUI;using ReactiveUI.Fody.Helpers;using StyleConfiguration.Config;namespace StyleConfiguration.ViewModels{    public class MainWindowViewModel : ReactiveObject, IScreen    {        public MainWindowViewModel()        {            ConfirmPassword = ReactiveCommand.CreateFromTask(async () =>            {                var reader = new UserChecker(UserPassword, UserLogin);                if (reader.CmpUsers() == true) await Router.Navigate.Execute(new WorkPlaceViewModel(reader.PathToConfig));                else                 {                    await MessageBox.Avalonia.MessageBoxManager                        .GetMessageBoxStandardWindow("Неверные данные", "Неправильно введен логин или пароль").Show();                    UserLogin = "";                    UserPassword = "";                }            });        }        public ReactiveCommand<Unit, Unit> ConfirmPassword { get; }        [Reactive] public string UserLogin { get; set; }        [Reactive] public string UserPassword { get; set; }        public RoutingState Router { get; } = new();    }}