using System.Reactive;using Avalonia.Interactivity;using ReactiveUI;using ReactiveUI.Fody.Helpers;namespace StyleConfiguration.ViewModels{    public class MainWindowViewModel : ReactiveObject, IScreen    {        public MainWindowViewModel()        {            ConfirmPassword = ReactiveCommand.CreateFromObservable(                () => Router.Navigate.Execute(new WorkPlaceViewModel())                );        }        public ReactiveCommand<Unit, IRoutableViewModel> ConfirmPassword { get; }        [Reactive] public string UserLogin { get; set; }        [Reactive] public string UserPassword { get; set; }        public RoutingState Router { get; } = new();    }}