using System.Collections.Generic;using System.Collections.ObjectModel;using System.Reactive;using System.Threading.Tasks;using Avalonia.Controls;using DynamicData;using ReactiveUI;namespace StyleConfiguration.ViewModels;public class WorkPlaceViewModel : ReactiveObject, IRoutableViewModel, IScreen{    public ObservableCollection<MenuItem> Items { get; }    public WorkPlaceViewModel(IReadOnlyCollection<MenuItem> items)    {         Items = new ObservableCollection<MenuItem>(items);    }      public ReactiveCommand<string, Unit> ButtonClick;    public string? UrlPathSegment { get; }    public IScreen? HostScreen { get; }    public RoutingState Router { get; } = new();}